var fs              = require('fs'),
    EventEmitter    = require('events').EventEmitter,
    exec            = require('child_process').exec;

var ee = new EventEmitter


var fish = function (query) {
    
    var ls = exec('ls ' + query, function (error, stdout, stderr) {
        ee.emit('ls', stdout);
    })
    return function (callback) {
        ee.on('ls', callback);
    };
};


































module.exports = fish;
